    private void cong(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cong
        int s1 = Integer.parseInt(numbera.getText());
        int s2 = Integer.parseInt(numberb.getText());
        int tong = s1 + s2 ;
        kq.setText("Kết quả là : " + tong);
        
    }//GEN-LAST:event_cong

    private void tru(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tru
        int s1 = Integer.parseInt(numbera.getText());
        int s2 = Integer.parseInt(numberb.getText());
        int tong = s1 - s2 ;
        kq.setText("Kết quả là : " + tong);
    }//GEN-LAST:event_tru

    private void nhan(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nhan
        int s1 = Integer.parseInt(numbera.getText());
        int s2 = Integer.parseInt(numberb.getText());
        int tong = s1 * s2 ;
        kq.setText("Kết quả là : " + tong);
    }//GEN-LAST:event_nhan

    private void chia(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_chia
        int s1 = Integer.parseInt(numbera.getText());
        int s2 = Integer.parseInt(numberb.getText());
        int tong = s1 / s2 ;
        kq.setText("Kết quả là : " + tong);
    }//GEN-LAST:event_chia

