    private void checkchiaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkchiaActionPerformed
        int s1 = Integer.parseInt(numbera.getText());
        int s2 = Integer.parseInt(numberb.getText());
        if (s1 / s2 == 0 ) {
            checktotal.setText("A CHIA HET CHO B ");
        }
        else {
            checktotal.setText("A KHONG CHIA HET CHO B ");
        }
    }//GEN-LAST:event_checkchiaActionPerformed

